import axios from "axios";
import React, { useState } from "react";
import {
  StyledFormWrapper,
  StyledForm,
  StyledInput,
  StyledButton,
  Styledh1,
  StyledFormContainer,
  StyledError,
  Notificationvert,
  Notificationrouge,
  StyledArea,
  ImgProduct,
} from "./OrderElements.js";
const initalState = {
  postCode: "",
  district: "",
  country: "",
};

const Order = () => {
  const [state, setState] = useState(initalState);
  const handleInput = (e) => {
    const inputName = e.currentTarget.name;
    const value = e.currentTarget.value;
    setState((prev) => ({ ...prev, [inputName]: value }));
  };
  const handleSubmit = (e) => {
    const CurrentPanier = this.props.location.state;
    const nom = localStorage.getItem("nom");
    const email = localStorage.getItem("email");
    e.preventDefault();
    if (state.quantite < 1) {
      return;
    }
    axios
      .post(`https://managecartandorders.herokuapp.com/api/order/create`, {
        user: { name: nom, email: email },
        billingAddress: {
          name: nom,
          email: email,
          postCode: state.postCode,
          district: state.district,
          country: state.country,
        },
        items: CurrentPanier,
      })
      .then((response) => {})
      .catch((error) => {});
  };
  return (
    <div>
      <StyledFormWrapper>
        <StyledFormContainer>
          <StyledForm onSubmit={handleSubmit}>
            <Styledh1>Ajouter un produit!</Styledh1>
            <br />
            <label htmlFor="nom"></label>
            <StyledInput
              type="text"
              name="nom"
              value={state.nom}
              onChange={handleInput}
              placeholder="NumTel..."
            />
            <label htmlFor="type"></label>
            <StyledInput
              type="text"
              name="type"
              value={state.postCode}
              onChange={handleInput}
              placeholder="Wilaya"
            />
            <label htmlFor="prix"></label>
            <StyledInput
              type="text"
              name="prix"
              value={state.prix}
              onChange={handleInput}
              placeholder="Code postal..."
            />
            <label htmlFor="quantite"></label>
            <StyledInput
              type="text"
              name="quantite"
              value={state.quantite}
              onChange={handleInput}
              placeholder="QuantitÃ©..."
            />
            <label htmlFor="description"></label>
            <StyledArea
              type="text"
              name="description"
              value={state.description}
              onChange={handleInput}
              placeholder="Description..."
            />

            <label htmlFor="imageurl"></label>
            <StyledButton type="submit">Enregistrer le produit</StyledButton>
          </StyledForm>
        </StyledFormContainer>
      </StyledFormWrapper>
    </div>
  );
};

export default Order;
