import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
  ImgPanier,
  ProduitContainer,
  LeftSide,
  RightSide,
  Nomp,
  LeftSideInfo,
  ProductsContainer,
  RemoveProduit,
  HrReact,
  Remove_Prix,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  const [Products, setProducts] = useState([]);
  const [reload, setReload] = useState(true);
  const [CartItem, setCartItem] = useState(0);
  const [Montant, setMontant] = useState(0);
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      setCurrentPanier(reponse);
      console.log(reponse);
    };
    getPanier();
  }, [reload]);
  useEffect(() => {
    setProducts([]);
    CurrentPanier.map((id) => {
      console.log(id.qty);
      const idtoProduct = async () => {
        const reponse = await axios
          .get(
            `https://whispering-bastion-00988.herokuapp.com/api/produits/${id.product_id}`
          )
          .then((result) => {
            console.log(result.data);
            return result.data;
          })
          .catch((err) => {
            console.log(err.message);
          });
        console.log(reponse.prix);
        setMontant(id.qty * reponse.prix);
        if (!Products.includes(reponse)) {
          reponse.qtycmd = id.qty;
          setProducts((prevProducts) => [...prevProducts, reponse]);
        }
      };
      idtoProduct();
    });
    setCartItem(CurrentPanier.length);
  }, [CurrentPanier]);
  const SupprimerProduit = (product_id) => {
    const email = localStorage.getItem("email");
    console.log(product_id);
    console.log(email);
    console.log(typeof product_id);
    axios
      .delete(
        `https://managecartandorders.herokuapp.com/api/cart/deleteProduit`,
        { data: { email: email, product_id: product_id } }
      )
      .then((reponse) => {
        setReload(!reload);
        setTimeout(alert("Produit supprimer avec succès"), 3000);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <PanierWrapper>
      <PanierContainer>
        <LeftSide>
          <Nomp>Panier({CartItem} items)</Nomp>
          <br />
          <ProductsContainer>
            {Products.map((p, key) => {
              return (
                <div>
                  <ProduitItem>
                    <ProduitContainer>
                      <ImgPanier src={p.imageurl} />
                      <LeftSideInfo>
                        <Nomp>{p.nom}</Nomp>
                        <Nomp>Quantité : {p.qtycmd}</Nomp>
                        <Remove_Prix>
                          <RemoveProduit
                            onClick={() => {
                              SupprimerProduit(p.id);
                            }}
                          >
                            Supprimer le produit
                          </RemoveProduit>
                          <Nomp>Prix : {p.prix * p.qtycmd}$</Nomp>
                        </Remove_Prix>
                      </LeftSideInfo>
                    </ProduitContainer>
                  </ProduitItem>
                  <center>
                    <HrReact />
                  </center>
                </div>
              );
            })}
          </ProductsContainer>
        </LeftSide>
        <RightSide>
          <h3>Montant actuelle</h3>
          <h4>{Montant}</h4>
        </RightSide>
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
